mapper.select:
select COUNT(*) from zhaoling.post_db


         and user_name = #{user_name}


         and post_title like concat('%', #{post_title}, '%')


         and post_status1 = #{post_status1}


         and datediff(#{post_time}, post_time) <=  1  and datediff(#{post_time}, post_time)  >=  0


         and post_substance like concat('%',#{post_substance},'%')


         and lab_name = #{lab_name}


        and datediff(#{post_found_time}, post_found_time) <=  1 and datediff(#{post_found_time}, post_found_time)  >=  0


         and post_found_place like concat('%',#{post_found_place},'%')

select * from zhaoling.post_db



         and user_name = #{post.user_name}


         and post_title like concat('%', #{post.post_title}, '%')


         and post_status1 = #{post.post_status1}


         and datediff(#{post.post_time}, post_time) <=  1 and datediff(#{post.post_time}, post_time)  >=  0


         and post_substance like concat('%',#{post.post_substance},'%')


         and lab_name = #{post.lab_name}


        and datediff(#{post.post_found_time}, post_found_time) <=  1 and datediff(#{post.post_found_time}, post_found_time)  >=  0


         and post_found_place like concat('%',#{post.post_found_place},'%')


ORDER BY post_id desc
limit #{page},#{limit}
